import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o,c as e,a as _}from"./app-670a551f.js";const s={},n=_("p",null,"佛为海龙王说法印经一卷",-1),c=_("p",null,"大乘单译经",-1),l=_("p",null,"佛为海龙王说法印经一卷",-1),r=_("p",null,"唐三藏法师义净奉制译",-1),i=_("p",null,"佛为海龙王说法印经",-1),a=_("p",null,"如是我闻。一时薄伽梵。在海龙王宫。与大苾刍众千二百五十人俱。并与众多菩萨摩诃萨俱。尔时娑竭罗龙王。即从座起。前礼佛足白言。世尊。颇有受持少法得福多不。佛告海龙王。有四殊胜法。若有受持读诵解了其义。用功虽少获福甚多。即与读诵八万四千法藏。功德无异。云何为四。所谓念诵。诸行无常。一切皆苦。诸法无我。寂灭为乐。龙王当知。是谓四殊胜法。菩萨摩诃萨无尽法智。早证无生。速至圆寂。是故汝等常应念诵。尔时世尊。说是四句法印经时。彼诸声闻。大菩萨众。及天龙八部。阿苏罗揵达婆等。闻佛所说。皆大欢喜。信受奉行。",-1),d=[n,c,l,r,i,a];function p(u,h){return o(),e("div",null,d)}const x=t(s,[["render",p],["__file","佛为海龙王说法印经.html.vue"]]);export{x as default};
