import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o,c as e,a as _}from"./app-670a551f.js";const s={},n=_("p",null,"佛说诸法本经一卷",-1),l=_("p",null,"小乘阿含部",-1),c=_("p",null,"佛说诸法本经一卷",-1),i=_("p",null,"吴月支优婆塞支谦译",-1),d=_("p",null,"佛说诸法本经",-1),r=_("p",null,"闻如是。一时佛在舍卫国祇树给孤独园。佛告诸比丘。听吾说诸法本。对曰唯然。世尊曰。若有外道异学有来问者。何谓法本。当答言。欲为诸法本。何谓习。更为习。何谓同趣。痛为同趣。何谓致有。念为致有。何谓明道。思惟为明道。何谓第一。三昧为第一。何谓最上。智慧为最上。何谓牢固。解脱为牢固。何谓毕竟。泥洹为毕竟。",-1),p=_("p",null,"如是诸比丘。欲为诸法本。更为诸法习。痛为诸法同趣。念为诸法致有。思惟为诸法明道。三昧为诸法第一。智慧为诸法最上。解脱为诸法牢固。泥洹为诸法毕竟。",-1),u=_("p",null,"诸比丘当学是。常当有去家之想念。非常想念。非常苦想念。苦非身想念。秽食想念。不净想念。死亡想念。一切世间无乐想念。知世间邪正想念。别世间有无想念。世间所习所取欢乐变失及其归趣。当如事以正见知之。诸比丘念是为断爱弃欲。入正慧得苦际。佛说经已。皆欢喜奉行。",-1),a=[n,l,c,i,d,r,p,u];function h(f,m){return o(),e("div",null,a)}const k=t(s,[["render",h],["__file","佛说诸法本经.html.vue"]]);export{k as default};
